<template>
    <div id="pvoutput">
        <q-list>
            <q-item v-if="this.widget.settings.showCurrentGeneration || this.widget.settings.showTodayGeneration || this.widget.settings.showWeekGeneration || this.widget.settings.showLifetimeGeneration">
                <q-item-main><h5>Generation</h5></q-item-main>
            </q-item>
            <q-item v-if="this.widget.settings.showCurrentGeneration">
                <q-item-main>Current</q-item-main>
                <q-item-side right>{{ formatNumber(status.power_generation) }} <small class="measure">Watt</small></q-item-side>
            </q-item>
            <q-item v-if="this.widget.settings.showTodayGeneration">
                <q-item-main>Today<br/><small class="text-grey">Peak Power</small></q-item-main>
                <q-item-side right>{{ formatNumber(output.energy_generated) }} <small class="measure">kWh</small><br/><small class="text-grey">{{ formatNumber(output.peak_power) }} kWh at {{ output.peak_time }}</small></q-item-side>
            </q-item>
            <q-item v-if="this.widget.settings.showWeekGeneration">
                <q-item-main>Last 7 Days<br/><small class="text-grey">Record Efficiency</small></q-item-main>
                <q-item-side right>{{ formatNumber(weekstatistics.energy_generated) }} <small class="measure">kWh</small><br/><small class="text-grey">{{ formatNumber(weekstatistics.record_efficiency) }} kWh/kW</small></q-item-side>
            </q-item>
            <q-item v-if="this.widget.settings.showLifetimeGeneration">
                <q-item-main>Lifetime<br/><small class="text-grey">Record Efficiency</small></q-item-main>
                <q-item-side right>{{ megaWattHour(statistics.energy_generated) }} <small class="measure">MWh</small><br/><small class="text-grey">{{ formatNumber(statistics.record_efficiency) }} kWh/kW</small></q-item-side>
            </q-item>
            <q-item v-if="this.widget.settings.showCurrentConsumption || this.widget.settings.showTodayConsumption">
                <q-item-main><h5>Consumption</h5></q-item-main>
            </q-item>
            <q-item v-if="this.widget.settings.showCurrentConsumption">
                <q-item-main>Current</q-item-main>
                <q-item-side right>{{ formatNumber(status.power_consumption) }} <small class="measure">Watt</small></q-item-side>
            </q-item>
            <q-item v-if="this.widget.settings.showTodayConsumption">
                <q-item-main>Today</q-item-main>
                <q-item-side right>{{ formatNumber(output.energy_used) }} <small class="measure">kWh</small></q-item-side>
            </q-item>
        </q-list>
    </div>
</template>

<script>
export default {
  props: ['widget'],
  data () {
    return {
      status: {},
      statistics: {},
      weekstatistics: {},
      output: {},
      intervalStatus: null,
      intervalStatistics: null,
      intervalWeekStatistics: null,
      intervalOutput: null
    }
  },
  created: function () {
    this.getStatus()
    this.getStatistics()
    this.getWeekStatistics()
    this.getOutput()
    this.intervalStatus = setInterval(() => {
      this.getStatus()
    }, 3600000)
    this.intervalStatistics = setInterval(() => {
      this.getStatistics()
    }, 3600000)
    this.intervalWeekStatistics = setInterval(() => {
      this.getWeekStatistics()
    }, 1800000)
    this.intervalOutput = setInterval(() => {
      this.getOutput()
    }, 300000)
  },
  methods: {
    getStatus () {
      this.axios.get('https://cors-anywhere.herokuapp.com/https://pvoutput.org/service/r2/getstatus.jsp?key=' + this.widget.settings.apikey + '&sid=' + this.widget.settings.systemid)
        .then((response) => {
          var data = response.data.split(',')
          this.status = {
            time: data[1],
            energy_generation: data[2],
            power_generation: data[3],
            energy_consumption: data[4],
            power_consumption: data[5],
            temperature: data[7]
          }
        })
        .catch((error) => {
          console.error(error)
        })
    },
    getStatistics () {
      this.axios.get('https://cors-anywhere.herokuapp.com/https://pvoutput.org/service/r2/getstatistic.jsp?key=' + this.widget.settings.apikey + '&sid=' + this.widget.settings.systemid)
        .then((response) => {
          var data = response.data.split(',')
          this.statistics = {
            energy_generated: data[0],
            energy_exported: data[1],
            avg_generation: data[2],
            min_generation: data[3],
            max_generation: data[4],
            avg_efficiency: data[5],
            outputs: data[6],
            date_from: data[7],
            date_to: data[8],
            record_efficiency: data[9],
            record_date: data[10]
          }
        })
        .catch((error) => {
          console.error(error)
        })
    },
    getWeekStatistics () {
      var d = new Date()
      var lastWeek = new Date(d.getFullYear(), d.getMonth(), d.getDate() - 7)
      var lastweekYear = lastWeek.getFullYear()
      var lastweekMonth = ('0' + (lastWeek.getMonth() + 1)).slice(-2)
      var lastweekDay = ('0' + lastWeek.getDate()).slice(-2)
      var lastweekDate = lastweekYear + lastweekMonth + lastweekDay
      var year = d.getFullYear()
      var month = ('0' + (d.getMonth() + 1)).slice(-2)
      var day = ('0' + d.getDate()).slice(-2)
      var date = year + month + day

      this.axios.get('https://cors-anywhere.herokuapp.com/https://pvoutput.org/service/r2/getstatistic.jsp?key=' + this.widget.settings.apikey + '&sid=' + this.widget.settings.systemid + '&df=' + lastweekDate + '&dt=' + date)
        .then((response) => {
          var data = response.data.split(',')
          this.weekstatistics = {
            energy_generated: data[0],
            energy_exported: data[1],
            avg_generation: data[2],
            min_generation: data[3],
            max_generation: data[4],
            avg_efficiency: data[5],
            outputs: data[6],
            date_from: data[7],
            date_to: data[8],
            record_efficiency: data[9],
            record_date: data[10]
          }
        })
        .catch((error) => {
          console.error(error)
        })
    },
    getOutput () {
      var d = new Date()
      var year = d.getFullYear()
      var month = ('0' + (d.getMonth() + 1)).slice(-2)
      var day = ('0' + d.getDate()).slice(-2)
      var date = year + month + day

      this.axios.get('https://cors-anywhere.herokuapp.com/https://pvoutput.org/service/r2/getoutput.jsp?key=' + this.widget.settings.apikey + '&sid=' + this.widget.settings.systemid + '&df=' + date)
        .then((response) => {
          var data = response.data.split(',')
          this.output = {
            energy_generated: data[1],
            efficiency: data[2],
            energy_exported: data[3],
            energy_used: data[4],
            peak_power: data[5],
            peak_time: data[6],
            condition: data[7]
          }
        })
        .catch((error) => {
          console.error(error)
        })
    },
    formatNumber: function (number) {
      if (number) {
        if (this.widget.settings.numberformat === 'us') {
          return number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',')
        } else {
          return number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.')
        }
      }
    },
    megaWattHour: function (number) {
      var mwh = number / 1000000
      return Math.round(mwh)
    }
  },
  beforeDestroy () {
    clearInterval(this.intervalStatus)
    clearInterval(this.intervalStatistics)
    clearInterval(this.intervalWeekStatistics)
    clearInterval(this.intervalOutput)
  }
}
</script>

<style lang="stylus" scoped>
    @import '~variables'

    #pvoutput
        background-image: url('data:image/png;base64,')
        background-repeat: no-repeat
        background-position: 80% 20%

    #pvoutput h5
        margin-top: 0
        margin-bottom: 0

    #pvoutput .q-item
        font-size: 1.4rem
        padding: 0 16px 10px

    #pvoutput .q-item small, #pvoutput .q-item .measure
        font-size: 1rem

    #pvoutput .q-item-side-right
        display: inline-block

    #pvoutput .q-item-side
        color: #fff
</style>
